SteamUser.prototype.inviteToGroup = function(userSteamID, groupSteamID) {
	var buffer = new ByteBuffer(17, ByteBuffer.LITTLE_ENDIAN);
	buffer.writeUint64(Helpers.steamID(userSteamID).toString());
